import React, { Component} from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';


const Course = props => (
    <tr>
      <td>{props.course.name}</td>
      <td>{props.course.location}</td>
      <td>
        <Link to={"/edit/"+props.course._id}>edit</Link> | <a href="#" onClick={() => { props.deleteCourse(props.course._id) }}>delete</a>
      </td>
    </tr>
)

export default class CourseList extends Component {

    constructor(props) {
        super(props);

        this.deleteCourse = this.deleteCourse.bind(this);

        this.state = {courses: [] };
    }

    componentDidMount() {
        axios.get('http://localhost:5000/courses/')
        .then(response => {
            this.setState({ courses: response.data})
        })
        .catch((error) => {
            console.log(error);
        })
    }

    deleteCourse(id) {
        axios.delete('http://localhost:5000/courses/' + id)
        .then(res => console.log(res.data));

        this.setState({
            courses: this.state.courses.filter(el => el._id !== id)
        })
    }

    scorecardList() {
        return this.state.scorecards.map(currentscorecard => {
          return <Scorecard scorecard={currentscorecard} deleteScorecard={this.deleteScorecard} key={currentscorecard._id}/>;
        })
      }

    render() {
        return (
          <div>
            <h3>Logged Scorecards</h3>
            <table className="table">
              <thead className="thead-light">
                <tr>
                  <th>Username</th>
                  <th>Course</th>
                  <th>Score</th>
                  <th>Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                { this.scorecardList() }
              </tbody>
            </table>
          </div>
        )
      }
}
