import React, { Component} from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import axios from 'axios';

export default class EditCourse extends Component {

    constructor(props) {
        super(props);

        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeLocation = this.onChangeLocation.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            location: '',
        }
    }

    //temporary
    componentDidMount() {

        axios.get('http://localhost:5000/courses/'+this.props.match.params.id)
        .then(response => {
            this.setState({
                name: response.data.name,
                location: response.data.location,
            })
        })
        .catch(function (error) {
            console.log(error);
        })
    }

    onChangeName(e) {
        this.setState({
            name: e.target.value
        })
    }

    onChangeLocation(e) {
        this.setState({
            location: e.target.value
        })
    }

    onSubmit(e) {
        e.preventDefault();

        const course = {
            name: this.state.name,
            location: this.state.location
        }

        console.log(course);

        axios.post('http://localhost:5000/courses/update/'+this.props.match.params.id, course)
        .then(res => console.log(res.data))
        .catch((error) => {
          console.log('Hi:' + error);
        })

        window.location = '/courselist';
    }

    render() {
        return (
        <div>
          <h3>Change Courses</h3>
          <form onSubmit={this.onSubmit}>
                <div className="form-group"> 
                <label>Name: </label>
                <input  type="text"
                    required
                    className="form-control"
                    value={this.state.name}
                    onChange={this.onChangeName}
                    />
                </div>
            <div>
                
            </div>
            <div className="form-group"> 
              <label>Location: </label>
              <input  type="text"
                  required
                  className="form-control"
                  value={this.state.location}
                  onChange={this.onChangeLocation}
                  />
            </div>
    
            <div className="form-group">
              <input type="submit" value="Submit Changes" className="btn btn-primary" />
            </div>
          </form>
        </div>
        )
      }
}
