import React, { Component} from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import axios from 'axios';

export default class CreateCourse extends Component {

    constructor(props) {
        super(props);

        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeLocation = this.onChangeLocation.bind(this);
        this.onChangeHoles = this.onChangeHoles.bind(this);
        this.onAddHole = this.onAddHole.bind(this);
        this.onRemoveHole = this.onRemoveHole.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            location: '',
            holes: []
        }
    }

    componentDidMount() {
        
    }

    onChangeName(e) {
        this.setState({
            name: e.target.value
        })
    }

    onChangeLocation(e) {
        this.setState({
            location: e.target.value
        })
    }

    onAddHole = () => {
        this.setState({
            holes: this.state.holes.concat([{par: 3}])
        })
    }

    onRemoveHole = idx => () => {
        this.setState({
            holes: this.state.holes.filter((s, sidx) => idx !== sidx)
        })
    }

    onChangeHoles = idx => e => {
        const newHoles = this.state.holes.map((hole, sidx) => {
            if (idx !== sidx) return hole;
            return {...hole, par: e.target.value };
        });

        this.setState({holes: newHoles});
    }

    onSubmit(e) {
        e.preventDefault();

        const course = {
            name: this.state.name,
            location: this.state.location,
            holes: this.state.holes            
        }

        console.log(course);

        axios.post('http://localhost:5000/courses/add', course)
        .then(res => console.log(res.data));

        window.location = '/courselist';
    }

    render() {
        return (
        <div>
          <h3>Create New Course</h3>
          <form onSubmit={this.onSubmit}>
            <div className="form-group"> 
                <label>Name: </label>
                <input  type="text"
                    required
                    className="form-control"
                    value={this.state.name}
                    onChange={this.onChangeName}
                    />
                </div>
            <div>
                
            </div>
            <div className="form-group"> 
              <label>Location: </label>
              <input  type="text"
                  required
                  className="form-control"
                  value={this.state.location}
                  onChange={this.onChangeLocation}
                  />
            </div>

            {this.state.holes.map((hole, idx) => (
                <div className="hole" key={hole._id}>
                    <input
                        type="number"
                        value={hole.par}
                        onChange={this.onChangeHoles(idx)}
                    />
                    <button
                        type = "button"
                        onClick={this.onRemoveHole(idx)}
                        className="small"
                    >
                        -
                    </button>
                </div>
            ))}
            <button
                type="button"
                onClick={this.onAddHole}
                className="small"
            >
                Add Hole
            </button>

            <div className="form-group">
              <input type="submit" value="Create Course" className="btn btn-primary" />
            </div>
          </form>
        </div>
        )
      }
}
