import React, {useState, useEffect} from "react";
import {useAuth0} from "@auth0/auth0-react";
import LoginMessage from "./login-message.component";
import axios from 'axios';

const Profile = () => {
    const {isAuthenticated, user} = useAuth0();
    const [matched, setMatched] = useState(false);
    const [player, setPlayer] = useState(null);
    const [player_rounds, setPlayer_rounds] = useState(0);

    useEffect(() => {

    });

    const onSubmit = (e) => {
        e.preventDefault();
        //this code will only execute if the user is logged in AND the page has not
        //yet identified the corresponding user object associated with the user
        //IF the page has already identified the user, then we don't want to run this
        //again for performance reasons. 
        if (!matched && isAuthenticated) {
            const {nickname} = user;
            //load in user data 
            axios.get('http://localhost:5000/users/')
            .then(response => {
                let player_found = false;
                if (response.data.length > 0) {

                    //search through the users and find the one associated with our logged in user
                    for (let i = 0; i < response.data.length; i++) {
                        if (response.data[i].username == nickname) {
                            console.log("Player found in DB");
                            console.log(response.data[i]._id); //need this to edit the user later on
                            player_found = true;
                            setPlayer(response.data[i]); //save player object to state for later use
                            break;
                        }
                    }

                    //if we couldn't find the associated user, create a new user object
                    //this object is associated with the user's authentication credentials
                    //and will be used to store the user's statistics in the DB. 
                    if (!player_found) {
                        const user = {
                            username: nickname,
                            rounds_played: 0,
                        }
            
                        axios.post('http://localhost:5000/users/add', user)
                        .then(res => console.log(res.data));
                        player_found = true;
                    }
                }

                //if no users exist, then go ahead and create a new user object
                else {
                    const user = {
                    username: nickname,
                    rounds_played: 0,
                    }
        
                    axios.post('http://localhost:5000/users/add', user)
                    .then(res => console.log(res.data));
                    player_found = true;
                }
                setMatched(player_found);
                console.log(matched);
            })
            
        }
   
    }

    if (isAuthenticated) {
        const {nickname, email, last_login} = user;

        const test = matched;
        
        return (
            <div className = "debug">
                <div className ="profile_header">
                    <h2>Personal Information:</h2>
                </div>

                <div className = "personal_info">
                    <h3>Username: {nickname}</h3>
                    <h3>Email: {email}</h3>
                </div>

                <div className ="profile_header">
                    <h2>Stats:</h2>
                </div>

                <div className = "statistics">
                    <h3>Rounds Played: {test}</h3>
                    <h3>Best Score: </h3>
                    <h3>Average Score: </h3>
                    <h3>Worst Score: </h3>
                    <h3>Birdie Percentage: </h3>

                    <form onSubmit={onSubmit}>
                        <div className="top_margin">
                            <input type="submit" value="Start Tracking Stats" className="btn btn-primary"/>
                        </div>
                    </form>
                </div>

            </div>
        );
    }

    /*
    <div className="row">
        <pre className="col-12 text-light bg-dark p-4">
            {JSON.stringify(user, null, 2)}
        </pre>
    </div>

    */

    else {
        return (
            <div>
              <LoginMessage/>
            </div>
        )
    }
    
};

export default Profile;
