import React, { Component} from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

var test_holes = [
    {par: 'one'},
    {par: 'two'},
    {par: 'three'}
];

const Course = props => (
    <tr>
      <td>{props.course.name}</td>
      <td>{props.course.location}</td>
      <td>{props.course.holes.length}</td>
      <td>
        <Link to={"/editcourse/"+props.course._id}>edit</Link> | <a href="#" onClick={() => { props.deleteCourse(props.course._id) }}>delete</a>
      </td>
    </tr>
)

const Test = ({holes}) => (
  <> 
    {holes.map(hole => (
      <div className="hole" key={hole._id}>{hole.par}</div>
    ))}
  </>
)

export default class CourseList extends Component {

    constructor(props) {
        super(props);

        this.deleteCourse = this.deleteCourse.bind(this);

        this.state = {courses: [] };
    }

    componentDidMount() {
        axios.get('http://localhost:5000/courses/')
        .then(response => {
            this.setState({ courses: response.data})
        })
        .catch((error) => {
            console.log(error);
        })
    }

    deleteCourse(id) {
        axios.delete('http://localhost:5000/courses/' + id)
        .then(res => console.log(res.data));

        this.setState({
            courses: this.state.courses.filter(el => el._id !== id)
        })
    }

    courseList() {
        return this.state.courses.map(currentcourse => {
          return <Course course={currentcourse} deleteCourse={this.deleteCourse} key={currentcourse._id}/>;
        })
      }

    render() {
        return (
          <div>
            <h3 >Course List</h3>
            <table className="table">
              <thead className="thead-light">
                <tr>
                  <th>Name</th>
                  <th>Location</th>
                  <th>Holes</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                { this.courseList() }
              </tbody>
            </table>
            <div>
            <Test holes={test_holes}/>
            </div>
          </div>
        )
      }
}
