import React, { Component} from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import axios from 'axios';

export default class EditScorecard extends Component {

    constructor(props) {
        super(props);

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeCourse = this.onChangeCourse.bind(this);
        this.onChangeScore = this.onChangeScore.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.updateScores = this.updateScores.bind(this);
        this.onChangeScores = this.onChangeScores.bind(this);

        this.state = {
          username: '',
          course: '',
          score: 0,
          numHoles: 0,
          par: 0,
          scores: [],
          date: new Date(),
          users: [],
          courses: [],
          holes: []
        }
    }
    //
    componentDidMount() {

      //load in scorecard data from selected scorecard
        axios.get('http://localhost:5000/scorecards/'+this.props.match.params.id)
        .then(response => {
            this.setState({
                username: response.data.username,
                course: response.data.course,
                score: response.data.score,
                scores: response.data.scores,
                date: new Date(response.data.date),
                par: response.data.par,
            })
        })
        .catch(function (error) {
            console.log(error);
        })

        //load in user data for dropdown select
        axios.get('http://localhost:5000/users/')
        .then(response => {
          if (response.data.length > 0) {
            this.setState({
              users: response.data.map(user => user.username)
            })
          }
        })

        //load in course data for dropdown select
        axios.get('http://localhost:5000/courses/')
        .then(response => {
          if (response.data.length > 0) {
            this.setState(state => ({...state,
              courses: response.data,
              course: response.data[0].name,
              numHoles: response.data[0].holes.length,
              scores: response.data[0].holes,
              holes: response.data[0].holes,
            }), () => {
              //callback function to ensure state is updated before elements are rendered
            })
          }
        })
    }


    //loops through the courses array to find the one the user selected
    //then updates the state with the corresponding values for numHoles
    //as well as copying the holes array into the scores array
    //so that the form elements for the individual scores can be rendered
    //NOTE: numHoles doesn't really need to be updated, just used for testing purposes
    updateScores() {
      //set the number of holes based on which course is selected
      for (let i = 0; i < this.state.courses.length; i++) {
        if (this.state.courses[i].name == this.state.course) {
          this.setState(state=> ({...state,
            numHoles: this.state.courses[i].holes.length, 
            scores: this.state.courses[i].holes,
            holes: this.state.course[i].holes}), () => {
            //callback function to ensure state is updated before elements are rendered
          });
        }
      }
    }

    onChangeScores = idx => e => {
      const newScores = this.state.scores.map((score, sidx) => {
          if (idx !== sidx) return score;
          return {...score, par: e.target.value };
      });

      this.setState(state => ({...state, scores: newScores}), () => {
        //callback function to ensure state is updated before elements are rendered
      });
  }

    onChangeUsername(e) {
        this.setState({
            username: e.target.value
        })
    }

    onChangeCourse(e) {
      this.setState(state => ({...state, course: e.target.value}), () => {
        this.updateScores();
      })
  }

    onChangeScore(e) {
        this.setState({
            score: e.target.value
        })
    }

    onChangeDate(date) {
        this.setState({
            date: date
        })
    }

    onSubmit(e) {
        e.preventDefault();

        //calculate the total score based on the individual holes
        let temp = 0;
        let temp2 = 0;
        for (let i = 0; i < this.state.scores.length; i++) {
          temp = temp + parseInt(this.state.scores[i].par);
          temp2 = temp2 + parseInt(this.state.holes[i].par);
        }

        //set the score in the stat and wait to post the new
        //scorecard until the state is updated by using
        //callback function
        this.setState(state => ({...state, score: temp, par: temp2}), () => {
        
          const scorecard = {
            username: this.state.username,
            course: this.state.course,
            score: this.state.score,
            date: this.state.date,
            scores: this.state.scores,
            par: this.state.par
          }

          axios.post('http://localhost:5000/scorecards/update/'+this.props.match.params.id, scorecard)
          .then(res => console.log(res.data))
          .catch((error) => {
            console.log(error);
          })
        
        })

        window.location = '/scorecardlist';
    }

    render() {
        return (
        <div>
          <h3>Edit Scorecard Log</h3>
          <form onSubmit={this.onSubmit}>
            <div className="form-group"> 
              <label>Username: </label>
              <select ref="userInput"
                  required
                  className="form-control"
                  value={this.state.username}
                  onChange={this.onChangeUsername}>
                  {
                    this.state.users.map(function(user) {
                      return <option 
                        key={user}
                        value={user}>{user}
                        </option>;
                    })
                  }
              </select>
            </div>
            <div>
                
            </div>

            <div className="form-group"> 
              <label>Course: </label>
              <select ref="userInput"
                  required
                  className="form-control"
                  value={this.state.course}
                  onChange={this.onChangeCourse}>
                  {
                    this.state.courses.map(function(course) {
                      return <option 
                        key={course.name}
                        value={course.name}>{course.name}
                        </option>;
                    })
                  }
              </select>
            </div>

            <div className="form-group">
              <label>Date: </label>
              <div>
                <DatePicker
                  selected={this.state.date}
                  onChange={this.onChangeDate}
                />
              </div>
            </div>

            {this.state.scores.map((score, idx) => (
                <div className="score" key={score._id}>
                  <label>Hole {idx + 1} score: </label>
                    <div>
                      <input
                          type="number"
                          value={score.par}
                          onChange={this.onChangeScores(idx)}
                      />
                    </div>
                </div>
            ))}
    
            <div className="form-group">
              <input type="submit" value="Submit Changes" className="btn btn-primary" />
            </div>
          </form>
        </div>
        )
      }
}
